import style from './mystyle.module.css';
import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBRipple,
  MDBBtn
} from 'mdb-react-ui-kit';

function ProfileCard({professionalExperience, image, body}){
    //argument destructuring
    // const {professionalExperience} = props
    return (


            <MDBCard>
      <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
        <MDBCardImage src='./images/web-developer.png' fluid alt='...' />
        <a>
          <div className='mask' style={{ backgroundColor: 'rgba(251, 251, 251, 0.15)' }}></div>
        </a>
      </MDBRipple>
                <MDBCardTitle>{professionalExperience}</MDBCardTitle>
                <MDBCardBody>
                    <MDBCardText>{body}</MDBCardText>
                </MDBCardBody>
            </MDBCard>


    );
}

export default ProfileCard;
